import React from 'react';
import { connect } from 'react-redux';
import { Tabs, Tab, TabList, TabPanel } from 'react-tabs';
import { Link } from 'react-router-dom';

// import custom component
import OwlCarousel from '../features/owl-carousel';
import ProductNine from '../features/product/product-two';

// import Services & Actions
import { getProductsByCategory } from '../../services';
import { addToCart, toggleWishlist, addToCompare, showQuickViewModal } from '../../actions';

// import Slider Settings
import { featuredSlider } from "../settings";

// import Data
import _data from '../../mock_data/data';

function NewCollection ( props ) {
    const { addToCart, toggleWishlist } = props;
    let products = props.products;
    products = products.slice( 0, 37 );

    let featuredProducts = getFeaturedProducts( products );

    return (
        <div className="container">
            <h2 className="title text-center mb-4">New Arrivals</h2>

            <div className="col-lg-9 top-margin-products">
                    { _data.category.map( ( cat, index ) =>
                        <TabPanel key={ index }>
                            <OwlCarousel adClass="owl-simple carousel-euqal-height carousel-with-shadow" carouselOptions={ featuredSlider }>
                                { getProductsByCategory( getProductsByCategory( products, cat ), 'Clothing' ).map( ( item, index ) =>
                                    <ProductNine product={ item }
                                        adClass="text-center product-7"
                                        key={ index }
                                        onAddToCart={ addToCart }
                                        onToggleWishlist={ toggleWishlist }
                                        showQuickView={ showQuickViewModal }
                                    />
                                ) }
                            </OwlCarousel>
                        </TabPanel>
                    ) }
                </div>
            <div className="more-container text-center mt-2">
                <Link to={ `${process.env.PUBLIC_URL}/shop/sidebar/list` } className="btn btn-more"><span>show more</span></Link>
            </div>

        </div>
    )
}

const mapStateToProps = ( state, props ) => {
    return {
        products: state.data.products ? state.data.products : []
    }
}

export default connect(
    mapStateToProps, { addToCart, toggleWishlist }
)( NewCollection );
